package com.toone.system.generator.util;import org.apache.commons.lang3.StringUtils;import org.springframework.boot.jdbc.DataSourceBuilder;import org.sqlite.JDBC;import javax.sql.DataSource;/** * @author machenike */public class SqliteBuilder {    private String filePath;    private String url;    public static SqliteBuilder create() {        return new SqliteBuilder();    }    public SqliteBuilder filePath(String filePath) {        this.filePath = filePath;        return this;    }    public SqliteBuilder url(String url) {        this.url = url;        return this;    }    public DataSource build() {        if (StringUtils.isNotBlank(url)) {            return DataSourceBuilder.create().                    url(url).driverClassName(JDBC.class.getName()).build();        }        if (StringUtils.isNotBlank(filePath)) {            return DataSourceBuilder.create().                    url("jdbc:sqlite:" + filePath).driverClassName(JDBC.class.getName()).build();        }        return DataSourceBuilder.create().build();    }}